package com.food.framework.util;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;/** * Created by user on 16-11-19. */public class DateUtil {    /**     * 返回标准格式的时间字符串     * @param date     * @return     */    public static String toString(Date date) {        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss,SSS");        String result = simpleDateFormat.format(date);        return result;    }    /**     *     * @param date     * @return     * @throws ParseException     */    public static Date toDate(String date) throws ParseException {        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss,SSS");        Date result = simpleDateFormat.parse(date);        return result;    }}