package com.food.framework.web.support.annotation;import org.springframework.web.bind.annotation.ValueConstants;import java.lang.annotation.*;/** * Created by user on 16-12-4. */@Target(ElementType.PARAMETER)@Retention(RetentionPolicy.RUNTIME)@Documentedpublic @interface JsonParam {    String value() default "";    boolean required() default true;    String defaultValue() default ValueConstants.DEFAULT_NONE;}