package com.food.framework.work;import java.util.*;/** * Created by user on 16-12-23. */public class WorkerGroup {    /**     * 工作组人员列表     */    private List<IWorker> innerWorkerList = new ArrayList<IWorker>();    private WorkerGroup() {    }    public static WorkerGroup workerGroup = new WorkerGroup();    public static WorkerGroup getInstance() {        return workerGroup;    }    /**     * 增加一个工作人员     *     * @param worker     */    public void addWorker(IWorker worker) {        workerGroup.innerWorkerList.add(worker);    }    /**     * 减少一个工作人员     *     * @param worker     */    public void reduceWorker(IWorker worker) {        workerGroup.innerWorkerList.remove(worker);    }    /**     * 获取一个闲置人员     *     * @return     */    public IWorker getFreeWorker() {        for (IWorker worker : innerWorkerList) {            if (WorkState.isFreeState(worker.getWorkState())) {                return worker;            }        }        return null;    }}